	"draw-logistic-overlay" | 
	"hidden" | 
	"always-show" | 
	"hide-from-bonus-gui" |
	"hide-from-fuel-tooltip" |
	"not-stackable" | 
	"can-extend-inventory" | 
	"primary-place-result" | 
	"mod-openable" |
	"only-in-cursor" |
	"spawnable";
